mapper.select:
select 

from cardpassword
where id = #{id,jdbcType=INTEGER}
  and randomNo = #{randomno,jdbcType=VARCHAR}

mapper.delete:
delete from cardpassword
where id = #{id,jdbcType=INTEGER}
  and randomNo = #{randomno,jdbcType=VARCHAR}

mapper.update:
update cardpassword


    cardPwd = #{cardpwd,jdbcType=VARCHAR},


    money = #{money,jdbcType=DOUBLE},


    date = #{date,jdbcType=VARCHAR},


where id = #{id,jdbcType=INTEGER}
  and randomNo = #{randomno,jdbcType=VARCHAR}

update cardpassword
set cardPwd = #{cardpwd,jdbcType=VARCHAR},
  money = #{money,jdbcType=DOUBLE},
  date = #{date,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}
  and randomNo = #{randomno,jdbcType=VARCHAR}
